/*!
 * Filterable - jQuery Plugin
 */
;(function($){function scaleDown(){$('.work-list > .work-box, .work-list > .single').removeClass('current').addClass('not-current');$('.nav-folio > a').removeClass('current-li')}function show(category){scaleDown();$('#'+category).addClass('current-li');$('.'+category).removeClass('not-current');$('.'+category).addClass('current');if(category=="all"){$('.nav-folio > a').removeClass('current-li');$('#all').addClass('current-li');$('.work-list > .work-box, .work-list > .single').removeClass('current, not-current')}if(category=="all-masonry"){$('.nav-folio > a').removeClass('current-li');$('#all-masonry').addClass('current-li');$('.work-list > .work-box, .work-list > .single').removeClass('current, not-current')}}$(document).ready(function(){$('#all').addClass('current-li');$(".nav-folio > a").click(function(){show(this.id)})});})(jQuery);